import 'package:flutter/material.dart';
import '../models/feed.dart';
import '../widgets/feed_card.dart';

class HomeScreen extends StatelessWidget {
  const HomeScreen({super.key});

  // 더미 피드 데이터
  List<Feed> getDummyFeeds() {
    return [
      Feed(
        title: '감정 일기 작성 팁',
        content: '하루의 감정을 기록하는 3가지 방법을 소개합니다. 꾸준히 쓰는 습관이 감정 조절에 도움이 됩니다.',
        imageUrl: 'data:image/jpeg;base64,',
      ),
      Feed(
        title: '동료 소방관의 이야기',
        content: '극한 상황에서 회복력을 키운 동료의 경험담을 들어보세요. 공감과 위로가 담긴 이야기입니다.',
        imageUrl: 'https://source.unsplash.com/featured/?firefighter',
      ),
      Feed(
        title: '불면증 극복 훈련법',
        content: '잠 못 드는 밤, 시도해볼 수 있는 호흡 훈련과 명상 방법을 소개합니다.',
        imageUrl: 'https://source.unsplash.com/featured/?sleep',
      ),
    ];
  }

  @override
  Widget build(BuildContext context) {
    final feeds = getDummyFeeds();

    return Scaffold(
      appBar: AppBar(
        title: const Text('Fire Feed'),
        backgroundColor: Theme.of(context).colorScheme.primary,
      ),
      body: ListView.builder(
        itemCount: feeds.length,
        itemBuilder: (context, index) {
          return FeedCard(feed: feeds[index]);
        },
      ),
    );
  }
}
